import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { PlusIcon, CreditCardIcon, CheckIcon } from '@heroicons/react/24/outline';
import api from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';
import toast from 'react-hot-toast';

const Pagos = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedPago, setSelectedPago] = useState(null);
  const [filter, setFilter] = useState('pendientes'); // 'todos', 'pendientes', 'vencidos'
  const queryClient = useQueryClient();

  // Obtener pagos
  const { data: pagos, isLoading } = useQuery(
    'pagos',
    () => api.get('/pagos').then(res => res.data)
  );

  // Filtrar pagos según el estado seleccionado
  const filteredPagos = React.useMemo(() => {
    if (!pagos) return [];
    
    switch (filter) {
      case 'pendientes':
        return pagos.filter(pago => 
          pago.estado === 'Pendiente' || 
          pago.estado === 'Parcial' || 
          pago.estado === 'Vencido'
        );
      case 'vencidos':
        return pagos.filter(pago => pago.estado === 'Vencido');
      case 'todos':
        return pagos;
      default:
        return pagos;
    }
  }, [pagos, filter]);

  // Registrar pago flexible
  const registerPagoMutation = useMutation(
    (data) => api.put(`/pagos/${data.id}/registrar-pago-flexible`, data),
    {
      onSuccess: async (response) => {
        // Invalidar y refrescar los datos
        await queryClient.invalidateQueries('pagos');
        
        setIsModalOpen(false);
        setSelectedPago(null);
        
        // Mostrar mensaje de éxito
        let mensaje = 'Pago registrado exitosamente';
        if (response.data.excedente > 0 && response.data.aplicadoAMesesFuturos) {
          mensaje = `Pago registrado exitosamente. Excedente de $${response.data.excedente.toLocaleString()} aplicado a meses futuros.`;
        } else if (response.data.excedente > 0) {
          mensaje = `Pago registrado exitosamente. Excedente de $${response.data.excedente.toLocaleString()} disponible.`;
        }
        
        toast.success(mensaje);
        
        // Si el pago se completó completamente, mostrar el próximo pago
        if (response.data.pago.estado === 'Pagado' || response.data.pago.estado === 'Pagado con excedente') {
          // Buscar automáticamente el próximo pago pendiente
          try {
            const proximoPago = await api.get(`/pagos/proximo-pago/${response.data.pago.vivienda._id}`);
            if (proximoPago.data) {
              toast.success(`Próximo pago generado para ${proximoPago.data.vivienda.numero}`);
            }
          } catch (error) {
            console.log('No se pudo generar próximo pago automáticamente');
          }
        }
      },
      onError: (error) => {
        toast.error(error.response?.data?.message || 'Error al registrar pago');
      },
    }
  );

  const handleRegisterPago = (pago) => {
    setSelectedPago(pago);
    setIsModalOpen(true);
  };

  const handleSubmitPago = (formData) => {
    const pagoId = formData.pagoId || selectedPago._id;
    registerPagoMutation.mutate({
      id: pagoId,
      ...formData
    });
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedPago(null);
  };

  // Función para generar próximo pago
  const generarProximoPago = async (viviendaId) => {
    try {
      const response = await api.get(`/pagos/proximo-pago/${viviendaId}`);
      if (response.data) {
        queryClient.invalidateQueries('pagos');
        toast.success(`Próximo pago generado para ${response.data.vivienda.numero}`);
      }
    } catch (error) {
      toast.error('Error al generar próximo pago');
    }
  };

  // Función para obtener pagos pendientes de una vivienda
  const obtenerPagosPendientes = async (viviendaId) => {
    try {
      const response = await api.get(`/pagos/pendientes/${viviendaId}`);
      return response.data;
    } catch (error) {
      console.error('Error obteniendo pagos pendientes:', error);
      return [];
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-2xl font-bold text-gray-900">Pagos</h1>
        <p className="mt-1 text-sm text-gray-500">
          Gestiona los pagos de mantenimiento
        </p>
      </div>

      {/* Filtros */}
      <div className="flex space-x-4 items-center">
        <button
          onClick={() => setFilter('pendientes')}
          className={`px-4 py-2 rounded-md text-sm font-medium ${
            filter === 'pendientes'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          Pendientes y Vencidos
        </button>
        <button
          onClick={() => setFilter('vencidos')}
          className={`px-4 py-2 rounded-md text-sm font-medium ${
            filter === 'vencidos'
              ? 'bg-red-600 text-white'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          Solo Vencidos
        </button>
        <button
          onClick={() => setFilter('todos')}
          className={`px-4 py-2 rounded-md text-sm font-medium ${
            filter === 'todos'
              ? 'bg-gray-600 text-white'
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          Todos
        </button>
        
        <div className="ml-auto">
          <button
            onClick={() => {
              queryClient.invalidateQueries('pagos');
              toast.success('Lista actualizada');
            }}
            className="px-4 py-2 bg-green-600 text-white rounded-md text-sm font-medium hover:bg-green-700"
          >
            🔄 Actualizar
          </button>
        </div>
      </div>

      {/* Estadísticas rápidas */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-white p-4 rounded-lg shadow">
          <div className="text-sm text-gray-500">Total Pendientes</div>
          <div className="text-2xl font-bold text-yellow-600">
            {pagos?.filter(p => p.estado === 'Pendiente').length || 0}
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <div className="text-sm text-gray-500">Vencidos</div>
          <div className="text-2xl font-bold text-red-600">
            {pagos?.filter(p => p.estado === 'Vencido').length || 0}
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <div className="text-sm text-gray-500">Parciales</div>
          <div className="text-2xl font-bold text-blue-600">
            {pagos?.filter(p => p.estado === 'Parcial').length || 0}
          </div>
        </div>
        <div className="bg-white p-4 rounded-lg shadow">
          <div className="text-sm text-gray-500">Completamente Pagados</div>
          <div className="text-2xl font-bold text-green-600">
            {pagos?.filter(p => p.estado === 'Pagado' || p.estado === 'Pagado con excedente').length || 0}
          </div>
        </div>
      </div>

      {/* Tabla */}
      <div className="card">
        <div className="card-body">
          {/* Indicador de resultados */}
          <div className="mb-4 text-sm text-gray-600">
            Mostrando {filteredPagos?.length || 0} de {pagos?.length || 0} pagos
            {filter !== 'todos' && (
              <span className="ml-2 text-blue-600">
                (filtrado por: {filter === 'pendientes' ? 'Pendientes y Vencidos' : 'Solo Vencidos'})
              </span>
            )}
          </div>
          
          <div className="overflow-x-auto">
            <table className="table">
              <thead className="table-header">
                                 <tr>
                   <th className="table-header-cell">Vivienda</th>
                   <th className="table-header-cell">Residente</th>
                   <th className="table-header-cell">Período</th>
                   <th className="table-header-cell">Monto</th>
                   <th className="table-header-cell">Pagado</th>
                   <th className="table-header-cell">Saldo Pendiente</th>
                   <th className="table-header-cell">Total Adeudo</th>
                   <th className="table-header-cell">Períodos Pendientes</th>
                   <th className="table-header-cell">Estado</th>
                   <th className="table-header-cell">Vencimiento</th>
                   <th className="table-header-cell">Acciones</th>
                 </tr>
              </thead>
              <tbody className="table-body">
                                 {filteredPagos?.map((pago) => {
                   // Calcular información de atrasos usando los métodos del backend
                   const diasAtraso = pago.diasAtraso ? pago.diasAtraso() : 0;
                   const montoAtrasado = diasAtraso > 0 ? pago.monto : 0;
                   const recargoCalculado = pago.calcularRecargo ? pago.calcularRecargo() : 0;
                   const totalAtrasado = montoAtrasado + recargoCalculado;
                   
                   // Determinar períodos vencidos - corregir lógica
                   let mesesVencidos = 0;
                   if (diasAtraso > 0) {
                     // Si está vencido, calcular cuántos meses han pasado
                     const fechaLimite = new Date(pago.fechaLimite);
                     const hoy = new Date();
                     const diffMeses = (hoy.getFullYear() - fechaLimite.getFullYear()) * 12 + 
                                     (hoy.getMonth() - fechaLimite.getMonth());
                     mesesVencidos = Math.max(1, diffMeses); // Mínimo 1 mes si está vencido
                   }
                   
                   // Calcular saldo pendiente
                   const saldoPendiente = pago.monto - (pago.montoPagado || 0);
                  
                  return (
                    <tr key={pago._id} className="table-row">
                      <td className="table-cell font-medium">
                        {pago.vivienda?.numero}
                      </td>
                      <td className="table-cell">
                        {pago.residente?.nombre} {pago.residente?.apellidos}
                      </td>
                      <td className="table-cell text-sm">
                        {pago.fechaInicioPeriodo && pago.fechaFinPeriodo ? (
                          <>
                            {new Date(pago.fechaInicioPeriodo).toLocaleDateString()} - {new Date(pago.fechaFinPeriodo).toLocaleDateString()}
                          </>
                        ) : (
                          `${pago.mes}/${pago.año}`
                        )}
                      </td>
                      <td className="table-cell">
                        ${pago.monto?.toLocaleString()}
                      </td>
                      <td className="table-cell">
                        ${(pago.montoPagado || 0).toLocaleString()}
                      </td>
                                             <td className="table-cell">
                         <span className={`font-medium ${
                           saldoPendiente > 0 ? 'text-red-600' : 'text-green-600'
                         }`}>
                           ${saldoPendiente.toLocaleString()}
                         </span>
                       </td>
                       <td className="table-cell">
                         {totalAtrasado > 0 ? (
                           <div className="text-red-600">
                             <div className="font-medium">${totalAtrasado.toLocaleString()}</div>
                             <div className="text-xs">
                               {recargoCalculado > 0 && `+$${recargoCalculado.toLocaleString()} recargo`}
                             </div>
                           </div>
                         ) : (
                           <span className="text-green-600">$0</span>
                         )}
                       </td>
                       <td className="table-cell">
                         {mesesVencidos > 0 ? (
                           <span className="text-red-600 font-medium">
                             {mesesVencidos} mes{mesesVencidos > 1 ? 'es' : ''}
                           </span>
                         ) : (
                           <span className="text-green-600">Al día</span>
                         )}
                       </td>
                      <td className="table-cell">
                        <span className={`badge ${
                          pago.estado === 'Pagado' ? 'badge-success' :
                          pago.estado === 'Pagado con excedente' ? 'badge-success' :
                          pago.estado === 'Pendiente' ? 'badge-warning' :
                          pago.estado === 'Parcial' ? 'badge-info' :
                          pago.estado === 'Vencido' ? 'badge-danger' :
                          'badge-secondary'
                        }`}>
                          {pago.estado}
                        </span>
                      </td>
                      <td className="table-cell">
                        {new Date(pago.fechaLimite).toLocaleDateString()}
                        {pago.estaVencido && (
                          <span className="ml-2 text-xs text-red-600">
                            (Vencido)
                          </span>
                        )}
                      </td>
                      <td className="table-cell">
                        <div className="flex space-x-2">
                          <button
                            onClick={() => handleRegisterPago(pago)}
                            className="btn-primary btn-sm"
                            disabled={pago.estado === 'Pagado' || pago.estado === 'Pagado con excedente'}
                          >
                            Registrar Pago
                          </button>
                          {(pago.estado === 'Pagado' || pago.estado === 'Pagado con excedente') && (
                            <button
                              onClick={() => generarProximoPago(pago.vivienda._id)}
                              className="btn-secondary btn-sm"
                              title="Generar próximo pago"
                            >
                              ➕ Próximo
                            </button>
                          )}
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          
          {filteredPagos?.length === 0 && (
            <div className="text-center py-8">
              <div className="text-gray-500">
                {filter === 'pendientes' && 'No hay pagos pendientes'}
                {filter === 'vencidos' && 'No hay pagos vencidos'}
                {filter === 'todos' && 'No hay pagos registrados'}
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modal de Registro de Pago */}
      {isModalOpen && selectedPago && (
        <RegistrarPagoModal
          pago={selectedPago}
          onSubmit={handleSubmitPago}
          onClose={closeModal}
          isLoading={registerPagoMutation.isLoading}
          obtenerPagosPendientes={obtenerPagosPendientes}
        />
      )}
    </div>
  );
};

// Componente Modal para Registrar Pago Flexible
const RegistrarPagoModal = ({ pago, onSubmit, onClose, isLoading, obtenerPagosPendientes }) => {
  const [formData, setFormData] = useState({
    montoPagado: pago.monto || 0,
    metodoPago: 'Efectivo',
    referenciaPago: '',
    aplicarExcedenteAMesFuturo: false
  });
  const [pagosPendientes, setPagosPendientes] = useState([]);
  const [pagoSeleccionado, setPagoSeleccionado] = useState(pago);
  const [mostrarSeleccion, setMostrarSeleccion] = useState(false);

  // Cargar pagos pendientes cuando se abre el modal
  React.useEffect(() => {
    const cargarPagosPendientes = async () => {
      const pagos = await obtenerPagosPendientes(pago.vivienda._id);
      setPagosPendientes(pagos);
      setMostrarSeleccion(pagos.length > 1);
    };
    cargarPagosPendientes();
  }, [pago.vivienda._id, obtenerPagosPendientes]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      pagoId: pagoSeleccionado._id
    });
  };

  // Calcular total a pagar (monto + recargo si está vencido)
  const recargo = pagoSeleccionado.estaVencido ? (pagoSeleccionado.calcularRecargo?.() || 0) : 0;
  const totalAPagar = pagoSeleccionado.monto + recargo;
  const excedente = parseFloat(formData.montoPagado) > totalAPagar ? parseFloat(formData.montoPagado) - totalAPagar : 0;

  const seleccionarPago = (pagoSeleccion) => {
    setPagoSeleccionado(pagoSeleccion);
    setFormData(prev => ({
      ...prev,
      montoPagado: pagoSeleccion.monto || 0
    }));
    setMostrarSeleccion(false);
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
        <div className="mt-3">
          <h3 className="text-lg font-medium text-gray-900 mb-4">
            Registrar Pago Flexible
          </h3>
          
          {/* Selección de período si hay múltiples pagos pendientes */}
          {mostrarSeleccion && (
            <div className="mb-4 p-4 bg-yellow-50 rounded-lg border border-yellow-200">
              <h4 className="text-sm font-medium text-yellow-800 mb-2">
                Selecciona el período a pagar:
              </h4>
              <div className="space-y-2 max-h-32 overflow-y-auto">
                {pagosPendientes.map((pagoPendiente) => {
                  const diasAtraso = pagoPendiente.diasAtraso || 0;
                  const recargoPendiente = pagoPendiente.calcularRecargo ? pagoPendiente.calcularRecargo() : 0;
                  const totalPendiente = pagoPendiente.monto + recargoPendiente;
                  
                  return (
                    <button
                      key={pagoPendiente._id}
                      onClick={() => seleccionarPago(pagoPendiente)}
                      className={`w-full text-left p-2 rounded border ${
                        pagoSeleccionado._id === pagoPendiente._id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <div className="text-sm">
                        <div className="font-medium">
                          {pagoPendiente.mes}/{pagoPendiente.año}
                        </div>
                        <div className="text-xs text-gray-600">
                          ${pagoPendiente.monto.toLocaleString()}
                          {recargoPendiente > 0 && ` + $${recargoPendiente.toLocaleString()} recargo`}
                          {diasAtraso > 0 && ` (${diasAtraso} días atrasado)`}
                        </div>
                      </div>
                    </button>
                  );
                })}
              </div>
            </div>
          )}
          
          <div className="mb-4 p-4 bg-gray-50 rounded-lg">
            <p className="text-sm text-gray-600">
              <strong>Vivienda:</strong> {pagoSeleccionado.vivienda?.numero}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Residente:</strong> {pagoSeleccionado.residente?.nombre} {pagoSeleccionado.residente?.apellidos}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Período:</strong> {pagoSeleccionado.mes}/{pagoSeleccionado.año}
            </p>
            <p className="text-sm text-gray-600">
              <strong>Monto Base:</strong> ${pagoSeleccionado.monto?.toLocaleString()}
            </p>
            {recargo > 0 && (
              <p className="text-sm text-red-600">
                <strong>Recargo por atraso:</strong> ${recargo.toLocaleString()}
              </p>
            )}
            <p className="text-sm font-medium text-gray-800">
              <strong>Total a Pagar:</strong> ${totalAPagar.toLocaleString()}
            </p>
          </div>
          
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="form-label">Monto a Pagar</label>
              <input
                type="number"
                name="montoPagado"
                value={formData.montoPagado}
                onChange={handleChange}
                className="input"
                min={totalAPagar}
                step="0.01"
                required
              />
            </div>

            <div>
              <label className="form-label">Método de Pago</label>
              <select
                name="metodoPago"
                value={formData.metodoPago}
                onChange={handleChange}
                className="input"
                required
              >
                <option value="Efectivo">Efectivo</option>
                <option value="Transferencia">Transferencia</option>
                <option value="Tarjeta">Tarjeta</option>
                <option value="Cheque">Cheque</option>
                <option value="Otro">Otro</option>
              </select>
            </div>

            <div>
              <label className="form-label">Referencia de Pago</label>
              <input
                type="text"
                name="referenciaPago"
                value={formData.referenciaPago}
                onChange={handleChange}
                className="input"
                placeholder="Número de referencia, folio, etc."
              />
            </div>

            {excedente > 0 && (
              <div className="p-3 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Excedente detectado:</strong> ${excedente.toLocaleString()}
                </p>
                <div className="mt-2">
                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      name="aplicarExcedenteAMesFuturo"
                      checked={formData.aplicarExcedenteAMesFuturo}
                      onChange={handleChange}
                      className="mr-2"
                    />
                    <span className="text-sm text-blue-800">
                      Aplicar excedente a meses futuros
                    </span>
                  </label>
                </div>
              </div>
            )}

            <div className="flex justify-end space-x-3 pt-4">
              <button
                type="button"
                onClick={onClose}
                className="btn-secondary"
                disabled={isLoading}
              >
                Cancelar
              </button>
              <button
                type="submit"
                className="btn-primary"
                disabled={isLoading}
              >
                {isLoading ? 'Registrando...' : '✓ Registrar Pago'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Pagos;
